import React from "react";
import Die from "./Die"

export default function App() {

    function allNewDice() {
        let numbersArray = [];
        for (let i = 0; i < 10; i++) {
            numbersArray.push(Math.ceil(Math.random() * 6))
        }
        return numbersArray;
    }

    const [dice, setDice] = React.useState(allNewDice());

    const diceArray = dice.map(die => <Die value={die} />)

    return (
        <main>
            <div className="dice-container">
                {diceArray}
            </div>
