const styles = {
    backgroundColor: props.isHeld ? "#59E391" : "white"
}

    return (
        <div 
        className="die" 
        style={styles}
        onClick={props.hodl}
        >
            <h2 className="die-number">{props.value}</h2>
        </div>
    )
}
