import React from "react";
import Die from "./Die"
import {nanoid} from "nanoid";

export default function App() {

    function allNewDice() {
        let numbersArray = [];
        for (let i = 0; i < 10; i++) {
            numbersArray.push({
                value: Math.ceil(Math.random() * 6),
                isHeld: false,
                id: nanoid
            })
        }
        return numbersArray;
    }

    function rollDice() {
        setDice(allNewDice())
    }

    const [dice, setDice] = React.useState(allNewDice());
    const diceArray = dice.map(die => <Die value={die.value} />)

    return (
        <main>
            <div className="dice-container">
                {diceArray}
            </div>
<button className="roll-dice" onClick={rollDice}>Roll</button>
        </main>
    )
}
