import React from "react";
import Die from "./Die"
import { nanoid } from "nanoid";
import Confetti from "react-confetti";

export default function App() {


    function generateNewDie() {
        return {
            value: Math.ceil(Math.random() * 6),
            isHeld: false,
            id: nanoid()
        }
    }

    function allNewDice() {
        let numbersArray = [];
        for (let i = 0; i < 10; i++) {
            numbersArray.push(generateNewDie())
        }
        return numbersArray;
    }


    function rollDice() {
        let counter = 0;
        if (tenzies) {
            
            setTenzies(false);
            setDice(allNewDice());
        } else {
            counter++;
            setDice(oldDice => oldDice.map(die => {
                return die.isHeld ?
                    die :
                    generateNewDie();
            }))
        }
        

    }

    function holdDice(id) {
        setDice(oldDice => oldDice.map(die => {
            return die.id === id ?
                { ...die, isHeld: !die.isHeld } :
                die
        }))
    }

    const [dice, setDice] = React.useState(allNewDice());

    const diceArray = dice.map(die => <Die
        key={die.id}
        value={die.value}
        isHeld={die.isHeld}
        holdDice={() => holdDice(die.id)}
    />)

    const [tenzies, setTenzies] = React.useState(false);

    React.useEffect(() => {
        let value = dice[0].value;
        if (dice.every(die => die.value === value && die.isHeld)) {
            setTenzies(true);
            console.log("Successful!");
        }
    }, [dice])

    return (
        <main>

            <h1 className="title">Tenzies</h1>
            <p className="instruction">Roll untill all dice are the same. Click each die to freeze it at its current value between rolls.</p>



            <div className="dice-container">
                {diceArray}
            </div>
            <button className="roll-dice" onClick={rollDice}>{tenzies ? "New game" : "Roll"}</button>
            {tenzies && <Confetti 
             height='500px'
             width="600px"
            />}
        </main>
    )
}
